import { Router } from "express";
import { isAuth } from "../middlewares/isAuth.js";
const router = Router();
import { getUserProfile, loginUser, myProfile, updateProfilePicture, updateUser, } from "../controllers/userControllers.js";
import upload from "../middlewares/multer.js";
router.post("/login", loginUser);
router.get("/user/me", isAuth, myProfile);
router.get("/user/profile/:id", getUserProfile);
router.post("/user/update", isAuth, updateUser);
router.post("/user/update/profile-picture", isAuth, upload, updateProfilePicture);
export default router;
